<template>  <div class="box-article">    <div class="box-article-head">      <div class="title">{{ articleObj.title }}</div>      <div class="time">{{ timeTitle }} {{ articleObj.time }}</div>    </div>    <v-md-preview-html      :html="htmlstr"      preview-class="vuepress-markdown-body"      @copy-code-success="handleCopyCodeSuccess"    ></v-md-preview-html>  </div></template><script setup>import { ref, onMounted, reactive } from 'vue'import VueMarkdownEditor, { xss } from '@kangc/v-md-editor'import { message } from '@utils/globalFun'import { useRoute, useRouter } from 'vue-router'const route = useRoute()const router = useRouter()let htmlstr = ref('')let articleObj = reactive({})const timeTitle = ref('发布时间：')function handleCopyCodeSuccess() {  message.success('复制成功！')}onMounted(() => {  let id = route.params.id  if (id) {    let val = localStorage.getItem('article')    val = JSON.parse(val) || ''    if (val?._id === id) {      articleObj = val      htmlstr.value = xss.process(        VueMarkdownEditor.vMdParser.themeConfig.markdownParser.render(          val.content        )      )    }  } else {    message.warning('找不到内容，请重试！')    router.push('/home')  }})</script><style scoped lang="scss">.box-article {  box-sizing: border-box;  min-height: calc(100vh - 140px);  &-head {    display: flex;    background-color: #fff;    align-items: center;    padding: 10px;    .title {      width: 70%;      font-size: 20px;      font-weight: 700;      text-align: center;    }    .time {      flex: 1;      display: flex;      justify-content: flex-end;      color: #333;    }  }}</style>