<template>  <div class="box-home">    <div class="box-home-main">      <section        class="box-list-hor"        v-for="item in list"        :key="item._id"      >        <div class="box-list-hor-image">          <a-image            :src="imgurl"            fallback="data:image/png;base64,"          />        </div>        <div class="box-list-hor-content">          <div            class="box-list-hor-content-title"            @click.stop="handleClick('detail', item)"          >            {{ item.title }}          </div>          <div            class="box-list-hor-content-detail"            @click.stop="handleClick('detail', item)"          >            {{ item.content }}          </div>          <div class="box-list-hor-content-msg">            <div class="box-list-hor-content-msg-left">              <div class="left-dot"><fire-outlined /><span>22</span></div>              <div class="left-dot"><heart-outlined /><span>24</span></div>            </div>            <div class="box-list-hor-content-msg-right">              <field-time-outlined /><span>{{ item.time }}</span>            </div>          </div>        </div>      </section>    </div>    <div class="box-home-side"></div>  </div></template><script setup>import { onMounted, ref } from 'vue'import apis from '@http/index'import { useRouter } from 'vue-router'import { message, timeFormatYMDhms } from '@utils/globalFun'import {  FieldTimeOutlined,  FireOutlined,  HeartOutlined} from '@ant-design/icons-vue'import { test_img } from '@assets/imgs/index.js'const imgurl = test_imgconst router = new useRouter()let list = ref([])const handleGet = async () => {  try {    let res = await apis.sendGet()    if (res.code === 200 && res.data) {      res.data.forEach(item => {        item.time = item.createTime          ? timeFormatYMDhms(item.createTime)          : item.createTime      })      list.value = res.data      // message.success(res.message)      console.log(res.data)    } else {      message.error(res.message)    }  } catch (error) {    message.error(error)  }}onMounted(() => {  handleGet()})const handleClick = (type, val) => {  if (type === 'detail') {    localStorage.setItem('article', JSON.stringify(val))    router.push('/article/' + val._id)  }}</script><style scoped lang="scss">.box-home {  display: flex;  padding-top: 20px;  &-main {    width: 60%;    .box-list-hor {      margin-bottom: 20px;    }  }  &-side {    flex: 1;  }}.box-list-hor {  height: 200px;  display: flex;  background: #fff;  padding: 10px;  border-radius: 5px;  &:hover {    z-index: 2;    box-shadow: 0 15px 30px rgb(0 0 0 / 10%);    transform: translate3d(0, -2px, 0);    cursor: pointer;  }  &-image {    width: 40%;    height: 100%;    .ant-image {      height: 100%;      object-fit: contain;      img {        height: 100%;        border-radius: 5px;      }      &:hover {        border-radius: 5px;        .ant-image-mask {          border-radius: 5px;        }      }      .ant-image-mask {        border-radius: 5px;      }    }  }  &-content {    flex: 1;    display: flex;    flex-direction: column;    &-title,    &-detail {      overflow: hidden;      text-overflow: ellipsis;      white-space: nowrap;      text-align: center;      flex: 1;    }    &-title {      padding: 5px;      font-size: 16px;      font-weight: 700;    }    &-detail {      padding: 0px 5px 5px 5px;    }    &-msg {      height: 40px;      padding: 0 5px;      display: flex;      justify-content: space-between;      align-items: flex-end;      span {        margin-left: 3px;      }      &-left {        display: flex;        .left-dot {          margin-right: 10px;        }      }    }  }}</style>