<template>  <div class="box-container">    <div class="box-head">      <a-row>        <a-col          :flex="3"          align="middle"        >          <div class="box-head-nav box-head-logo">子墨一点</div>        </a-col>        <a-col :flex="6">          <div            class="box-head-nav box-head-menu"            v-for="menu in menuList"            :key="menu.path"            @click="handleClick(menu.path)"          >            {{ menu.name }}          </div>        </a-col>        <a-col :flex="3">          <div            class="box-head-cart"            v-if="!username"          >            <div              class="box-head-nav login"              @click="handleClick('send')"            >              发布            </div>            <div class="box-head-nav login">·</div>            <div              class="box-head-nav login"              @click="handleClick('login')"            >              登录            </div>            <div class="box-head-nav login">·</div>            <div              class="box-head-nav login"              @click="handleClick('register')"            >              注册            </div>          </div>          <div            class="box-head-cart"            v-else          >            <div              class="box-head-nav login"              @click="handleClick('send')"            >              发布            </div>            <div class="box-head-nav login">·</div>            <div class="box-head-nav login">              {{ username }}            </div>            <div class="box-head-nav login">·</div>            <div              class="box-head-nav login"              @click="handleClick('loginout')"            >              注销            </div>          </div>        </a-col>      </a-row>    </div>    <div class="box-content">      <div class="box-content-detail">        <RouterView />      </div>      <div class="box-footer">        <div class="desc">何以解忧，唯有轮子</div>        <div class="safe">备案号为：12345678</div>      </div>    </div>  </div></template><script setup>import { reactive, computed, nextTick } from 'vue'import { RouterView, useRouter } from 'vue-router'import { userStore } from '@/stores/index.js'let router = useRouter()const user_store = userStore()let username = computed(() => user_store.usermsg?.username)const menuList = reactive([  {    name: '首页',    path: 'home'  },  // {  //   name: '发布',  //   path: 'send'  // },  {    name: '服务',    path: 'help'  },  {    name: '留言',    path: 'remark'  }])// 处理点击事件const handleClick = type => {  if (type === 'loginout') {    localStorage.removeItem('userInfo')    user_store.userInfo = null    nextTick(() => {      router.push('/login')    })  } else {    if (type) {      router.push('/' + type)    }  }}</script><style scoped lang="scss">.box-container {  width: 100vw;  height: 100vh;  background-color: #f5f5f5; // rgba(229, 229, 229, 0.3);  // background-color: #F0F8FF;  display: flex;  flex-direction: column;  .box-head {    height: 40px;    background-color: #333;    .ant-row {      height: 100%;      width: 1220px;      margin: 0 auto;      .ant-col {        height: 100%;        display: flex;        div {          height: 100%;          display: flex;          align-items: center;          color: rgba(255, 255, 255, 0.8);        }        // .box-head-logo {        //     padding-left: 10px;        // }        .box-head-menu {          padding: 0 15px;        }        .box-head-cart {          width: 100%;          // padding-right: 20px;          display: flex;          justify-content: flex-end;          div {            margin-right: 10px;          }        }        .box-head-nav {          &:hover {            cursor: pointer;            color: #fff;          }        }      }    }  }  .box-content {    width: 100%;    flex: 1;    height: calc(100vh - 100px);    overflow: auto;    &-detail {      width: 1220px;      margin: 20px auto;      min-height: calc(100vh - 140px);    }  }  .box-footer {    height: 60px;    display: flex;    align-items: center;    justify-content: center;    background-color: rgba(200, 200, 200, 0.2);    color: gray;    font-weight: 500;    .desc {      margin-right: 10px;    }  }}/* 统一滚动条样式 */::-webkit-scrollbar {  width: 8px;  height: 8px;}::-webkit-scrollbar-thumb {  background-color: #c1c1c1;  border-radius: 4px;}::-webkit-scrollbar-track {  background-color: #f5f5f5;}</style>