<template>  <div class="box-send">    <div class="box-send-upload">      <a-upload-dragger        :fileList="fileList"        name="file"        :multiple="true"        :before-upload="beforeUpload"        @remove="handleRemove"      >        <p class="ant-upload-drag-icon">          <inbox-outlined></inbox-outlined>        </p>        <p class="ant-upload-text">点击选择或拖拽文件到这里上传</p>      </a-upload-dragger>    </div>    <a-button      type="primary"      @click="handleUpload"      >上传</a-button    >  </div></template><script setup>import { ref } from 'vue'import { InboxOutlined } from '@ant-design/icons-vue'import apis from '@http/index'const fileList = ref([])const handleRemove = file => {  const index = fileList.value.indexOf(file)  const newFileList = fileList.value.slice()  newFileList.splice(index, 1)  fileList.value = newFileList}const beforeUpload = file => {  fileList.value = [...fileList.value, file]  return false}const handleUpload = async () => {  if (fileList.value.length === 0) {    return ''  }  const formData = new FormData()  fileList.value.forEach(file => {    formData.append('file', file)  })  // uploading.value = true;  if (fileList.value.length > 1) {    formData.append('uploadType', 'more')  }  let imgArr = []  try {    let resUpload = await apis.sendUpload(formData)    let { code, data } = resUpload    if (code === 200 && data) {      if (Array.isArray(data)) {        imgArr = data      } else {        imgArr.push(data)      }    }    return imgArr  } catch (error) {    // message.error(message);    return imgArr  }}</script><style lang="scss">.box-send {  box-sizing: border-box;  &-content {    margin: 20px 0;  }}</style>