<template>  <div class="box-send">    <div class="box-send-title">      <a-textarea        v-model:value="title"        placeholder="请输入标题/关键词/描述......"        :auto-size="{ minRows: 2, maxRows: 5 }"      />      <a-button        type="primary"        @click="handleSend"        >发布</a-button      >    </div>    <div class="box-send-content">      <v-md-editor        v-model="text"        height="400px"        @copy-code-success="handleCopyCodeSuccess"      ></v-md-editor>    </div>    <div class="box-send-upload">      <a-upload-dragger        v-model:fileList="fileList"        name="file"        :multiple="true"        action="https://www.mocky.io/v2/5cc8019d300000980a055e76"        @change="handleChange"      >        <p class="ant-upload-drag-icon">          <inbox-outlined></inbox-outlined>        </p>        <p class="ant-upload-text">点击选择或拖拽文件到这里上传</p>      </a-upload-dragger>    </div>  </div></template><script setup>import { ref } from 'vue'// import VueMarkdownEditor, { xss } from '@kangc/v-md-editor'import { message } from '@utils/globalFun'import apis from '@http/index'import { InboxOutlined } from '@ant-design/icons-vue'let text = ref('')let title = ref('')function handleCopyCodeSuccess() {  message.success('复制成功！')}const handleSend = async () => {  let params = {    title: title.value,    content: text.value  }  console.log(params)  if (!params.title) {    message.error('请输入标题！')    return  }  if (!params.content) {    message.error('请输入内容！')    return  }  try {    let res = await apis.sendAdd(params)    console.log(res)    if (res.code === 200 && res.data) {      message.success(res.message)      title.value = ''      text.value = ''    } else {      message.success(res.message)    }  } catch (error) {    message.error(error)  }}</script><style lang="scss">.box-send {  padding-top: 10px;  &-title {    display: flex;    align-items: center;    .arco-textarea {      border: 1px solid #333;      border-radius: 5px;      &-focus {        border-radius: 5px;      }    }    .arco-textarea-wrapper {      margin-right: 10px;      &:focus-within {        border-radius: 5px;      }    }  }  &-content {    margin-bottom: 20px;  }}</style>