<template>  <div class="box-send-title">    <a-textarea      v-model:value="title"      placeholder="请输入标题/关键词/描述......"      :auto-size="{ minRows: 2, maxRows: 5 }"    />    <a-button      type="primary"      @click="handleSend"      >发布</a-button    >  </div>  <div class="box-md">    <CherryMD      v-model="mdContent"      @setHtml="getContent"      ref="cherrymdEdit"    />  </div></template><script setup>import CherryMD from '@/components/markdown/markdownIndex.vue'import { message } from '@utils/globalFun'import apis from '@http/index'import { ref } from 'vue'let title = ref('')let mdContent = ref('')const cherrymdEdit = ref(null)// 获取文章结构信息const getContent = content => {  mdContent.value = content}// 发布const handleSend = async () => {  let params = {    title: title.value,    content: mdContent.value  }  if (!params.title) {    message.error('请输入标题！')    return  }  if (!params.content) {    message.error('请输入内容！')    return  }  try {    params.image = []    let res = await apis.sendAdd(params)    console.log(res)    if (res.code === 200 && res.data) {      message.success(res.message)      title.value = ''      mdContent.value = ''      cherrymdEdit.value.setCherryContent(mdContent.value)    } else {      message.error(res.message)    }  } catch (error) {    message.error(error)  }}</script><style scoped lang="scss">.box-send-title {  width: 100%;  display: flex;  align-items: center;  justify-content: space-between;  .ant-input {    width: 93%;    border-radius: 5px;    &-focus {      border-radius: 5px;    }  }  .ant-btn {    border-radius: 8px;  }}.box-md {  margin-top: 10px;  height: calc(100vh - 140px);}</style>